const now=new Date,hours=now.getHours(),minutes=now.getMinutes(),seconds=now.getSeconds();let year=now.getFullYear(),month=now.getMonth()+1;const date=now.getDate();function fn(n){return(n<10?"0":"")+n}function nowTime(){return hours+":"+minutes+":"+seconds}function nowTimeW0(){return fn(hours)+":"+fn(minutes)+":"+fn(seconds)}function today(n){return year+n+month+n+date}function todayW0(n){return year+n+fn(month)+n+fn(date)}function week(){const n=now.getDay();return 0===n?n+7:n}function yearTotalDays(n){n&&(year=n);let t=0;for(let n=1;n<13;n++){t+=new Date(year,n,0).getDate()}return t}function monthTotalDays(n){return n&&(month=n),new Date(year,month,0).getDate()}function todayInYear(){return Math.ceil((now-new Date(year.toString()))/864e5)-1}
